    void init() {
        
        // Apply tileset palette to the engine as background palette
        pixelroot32::graphics::enableDualPaletteMode(true);
        
        pixelroot32::graphics::setBackgroundCustomPalette(CUSTOM_PALETTE_DATA);
        
        

        
        // Setup Background
        background.width = MAP_WIDTH;
        background.height = MAP_HEIGHT;
        background.tileWidth = TILE_SIZE;
        background.tileHeight = TILE_SIZE;
        background.indices = (uint8_t*)BACKGROUND_INDICES;
        
        background.tiles = TILESET_SPRITES;
        background.tileCount = 22;
        
        // Setup Platforms
        platforms.width = MAP_WIDTH;
        platforms.height = MAP_HEIGHT;
        platforms.tileWidth = TILE_SIZE;
        platforms.tileHeight = TILE_SIZE;
        platforms.indices = (uint8_t*)PLATFORMS_INDICES;
        
        platforms.tiles = TILESET_SPRITES;
        platforms.tileCount = 22;
        
        // Setup Stairs
        stairs.width = MAP_WIDTH;
        stairs.height = MAP_HEIGHT;
        stairs.tileWidth = TILE_SIZE;
        stairs.tileHeight = TILE_SIZE;
        stairs.indices = (uint8_t*)STAIRS_INDICES;
        
        stairs.tiles = TILESET_SPRITES;
        stairs.tileCount = 22;
        
        // Setup Details
        details.width = MAP_WIDTH;
        details.height = MAP_HEIGHT;
        details.tileWidth = TILE_SIZE;
        details.tileHeight = TILE_SIZE;
        details.indices = (uint8_t*)DETAILS_INDICES;
        
        details.tiles = TILESET_SPRITES;
        details.tileCount = 22;
        
    }

